import React, {Component} from "react"
import { isValidPhoneNumber } from "react-phone-number-input"

class LoginPartOne extends Component{

    constructor(props){
        super(props)
            this.state = {
                areacode: "+972",
                phonenumber: "",
                isValidPhoneNumber: true,
                stage: 1
            }
    }

    handleChange = (event) => {
        this.setState({
            [event.target.name] : event.target.value
        })
    }

    continueClicked = () => {

            if(isValidPhoneNumber(this.state.areacode + this.state.phonenumber)){
                this.setState(
                    {stage: 2})
            }else{
                this.setState({
                    isValidPhoneNumber: false
                })
            }
    }

    render(){
        return (

            <>

                <div>
                    <h1>המספר שלי הוא</h1>
                    {! this.state.isValidPhoneNumber && <div>יש להזין מספר טלפון חוקי</div>}
                    <input type="text" name="areacode" value={this.state.areacode} onChange={this.handleChange}></input>
                    <input type="text" name="phonenumber" value={this.state.phonenumber} onChange={this.handleChange}></input>
                    <button onClick={this.continueClicked}>המשך</button>
                </div>    

            </>

        )
        
    }

}

export default LoginPartOne
