import React from 'react'
import { Container , Row, Col, Image, Button } from "react-bootstrap"

const GridSystem = (props) => {

    let indexFriends =0
    
    const buildGrid = ()=> {

        return (renderRows())
    
    }

    const renderRows = () =>{
        let rows = []

        for( let row = 0; row < props.rowcount ; row++ ){

            rows.push(
                <Row className="Row">
                    {
                        renderCols()
                    }
               </Row>
            )
        }

        return rows;

    }                               

    const renderCols = () =>{

        let cols = []

        for( let col=0; col < props.colCount && indexFriends < props.friends.length ; col++ ){

            if(props.friends!=null){
                cols.push(
                    <Col className="Col text-center">  
                        { 
                            <div className='wrapper'>
                                <div><Button className='c1'>cancel friendship</Button></div>
                                {/* <span className='me-5 '>{props.friends[indexFriends].username}</span> */}
                                <div><Image src={`data:image/jpeg;base64,${props.friends[indexFriends].image[0].content}`} className="grid-image c2 " id={props.friends[indexFriends++].id} /></div>
                            </div> 
                        }      
                    </Col>
                )
            }
        }

        return cols
}

    return (

        <Container className="container w-50 p-3 ">
            {
            buildGrid()
            }
        </Container>

    );
};

export default GridSystem;
