import React from 'react'
import { Container , Row, Col, Image } from "react-bootstrap"

const GridSystem = (props) => {

    let indexFriends =0
    
    const buildGrid = ()=> {

        return (renderRows())
    
    }

    const renderRows = () =>{
        let rows = []

        for( let row = 0; row < props.rowcount ; row++ ){

            rows.push(
                <Row className="Row">
                    {
                        renderCols()
                    }
               </Row>
            )
        }

        return rows;

    }                               

    const renderCols = () =>{

        let cols = []

        for( let col=0; col < props.colCount; col++ ){

            if(props.friends!=null){
                cols.push(
                    <Col className="Col text-center">  
                        { <Image src={`data:image/jpeg;base64,${props.friends[indexFriends].image[0].content}`} roundedCircle className="grid-image" id={props.friends[indexFriends++].id} /> }       
                    </Col>
                )
            }

       return cols
    }
}

    return (

        <Container className="container w-50 p-3 position-absolute container-grid-syatem">
            {
            buildGrid()
            }
        </Container>

    );
};

export default GridSystem;
