import React, { Component } from 'react'
import Xarrow from "react-xarrows";
import GridSystem from './GridSystem.js';
import DrawArrows from './DrawArrowsjs';

class ViewerNetworkVisualization extends Component {


    constructor(props){
        super(props)
            this.state = {
                conection: 
                        [  {flag : false, id: "1", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: null, flag : true, id: "2", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: "2", flag : true, id: "3", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: null, flag : true, id: "4", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {flag : false, id: "5", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {flag : false, id: "6", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: "3", flag : true, id: "7", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {flag : false, id: "8", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: "10", flag : true, id: "9", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: "4", flag : true, id: "10", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {flag : false, id: "11", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {flag : false, id: "12", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: "7", flag : true, id: "13", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: "9", flag : true, id: "14", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {flag : false, id: "15", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {flag : false, id: "16", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: "22", flag : true, id: "17", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: "17", flag : true, id: "18", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: "24", flag : true, id: "19", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {flag : false, id: "20", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {flag : false, id: "21", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: "26", flag : true, id: "22", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {flag : false, id: "23", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: "25", flag : true, id: "24", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: "30", flag:  true, id: "25", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: null,flag : true, id: "26", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {flag : false, id: "27", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {flag : false, id: "28", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {flag : false, id: "29", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {next: null,flag : true, id: "30", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'}
                         ],
                currentId : 0        
            }
    }

    render() {
        return (

            <>

                <GridSystem colCount={5} md={6}>
                {
                this.state.conection.map(node => { if(node.flag) {
                                                        return <img src={node.image} class="rounded-circle" id={node.id} style={{width: "50px", height: "50px", margin: "20px"}} ></img>
                                                   }
                                                   else {return <div> </div>}}) 
                }
                </GridSystem>

                <DrawArrows>
                    this.state.conection
                </DrawArrows>

                {/* {
                    this.state.conection.map(node => {
                        if(node.flag && node.next!=null){
                            return (<Xarrow
                                        start={node.id} //can be react ref
                                        end= {node.next} react ref
                                    />)
                        }
                    })
                } */}

            </>
        )
    }
}

export default ViewerNetworkVisualization
