import React, { Component } from 'react'
import UserInfoForm from './UserInfoForm'
import moment from 'moment'
import {User} from "./User"
import UserDataService from "../api/user/UserDataService";

class UserInfoUpdate extends Component {

    constructor(props){
        super(props)
            this.state = {
                title: "עדכון פרטים",
                user: new User("","","","","","")
            }
    }

    componentDidMount(){
        this.refreshUsers();
    }

    refreshUsers(){
        UserDataService.retrieveAllUsers()
        .then(
            response=> {
           this.setState({   
                   users:  new User(response.data.id,
                                    response.data.username,
                                    response.data.gender,
                                    response.data.date,
                                    response.data.phoneNumber,
                                    response.data.email)
               })
            }
        )
    }

    render() {

      let title = "עדכון פרטים";

      return (

          <>
       
            <UserInfoForm User={this.state.user} title={this.state.title}/>
          </>

      )

    }
}

export default UserInfoUpdate
