import React from 'react'
import Xarrow from "react-xarrows";

const DrawArrows = ({children}) => {

let index = 0;    

renderXarrow = () =>{

    let xarrow = [];

    for(let arrow = 0; arrow <children.length;arrow++){

        xarrow.push(

            <Xarrow
            start={children[index].id} //can be react ref
            end= {children[index].next}/>

        )

    }

    return xarrow;

}

return renderXarrow();

};

export default DrawArrows;
