import moment from 'moment'

class DateHelpers{

    getTodayEighteenYearsAgo(){

        // let currentDate = moment(new Date()).format('YYYY-MM-DD')

        // return currentDate.setFullYear(currentDate.getFullYear()-18)

        const date =  moment(new Date(new Date().setFullYear(new Date().getFullYear() - 18)))
        return date

    }

}

export default new DateHelpers()
