import React, {Component} from "react";
import UserDataService from "../../api/user/UserDataService";
import AuthenticationService from "@services/AuthenticationService.js"
import GridSystem from './component/GridSystem.js';

class ShowFriends extends Component {
  
  constructor(props){
    super(props)
        this.state = {
            id: AuthenticationService.getLoggedInUserId(),
            friends: null
        }
    }

  componentDidMount(){
    this.refresh()
  }

  refresh = async () =>{
    const res =  await UserDataService.getFriends(this.state.id);
    this.setState({friends : res.data})
  }

  get 


  render() {

    const colCount = 2
    let rowcount

    if(this.state.friends!=null){
     rowcount = Math.ceil(this.state.friends.length / colCount) ;
    }else{
      rowcount = 0;
    }

    return (

      <div className="mx-auto">
               <GridSystem rowcount = {rowcount} colCount = {colCount} friends={this.state.friends} />
      </div>

    );
  }
}

export default ShowFriends
