import React, {Component} from "react"
import AuthenticationService from "@services/AuthenticationService.js"
import { FloatingLabelsPassword, FloatingLabelsPhone, FloatingLabelsEmail } from '@components/forms/floating_labels/index.js'
import {Container, Row, Col, Nav} from 'react-bootstrap'


class Login extends Component{

    constructor(props){
        super(props)
            this.state = {
                areacode: '+972',
                phone: '',
                email: '',
                isValidPhoneNumber: true,
                password: '',
                isvalidPassword: true,
            }
    }

    handleChange = (event) => {
        this.setState({
            [event.target.name] : event.target.value
        })
    }

    continueClicked = async (event) => {

            //if(AuthenticationService.executeAuthenticationService(this.state.phonenumber, this.state.password)){
                AuthenticationService.registerSuccessfulLogin(this.state.phonenumber)
                this.props.history.push('/search/match/')
            //}
            
            this.setState({  isvalidPassword: false })

        }



    render(){
        return (

            <>
            
                <Container className='mt-5'>

                    <Row className="justify-content-md-center">

                        <Col lg="4">
                            <p class="fs-3">Enter your number phone:</p>
                        </Col>

                    </Row>

                    <Row className="justify-content-md-center">

                        <Col lg="4">
                            <FloatingLabelsPhone handleChange={this.handleChange} val={this.state.phone} name="phone"/>
                        </Col>

                    </Row>

                    <Row className="justify-content-md-center bg-success">

                        <Col lg="4">
                            <p class="fs-3"> Or enter your email:</p>
                        </Col>

                    </Row>

                    <Row className="justify-content-md-center">

                        <Col lg="4">
                            <FloatingLabelsEmail handleChange={this.handleChange} val={this.state.phone} name="email"/>
                        </Col>

                    </Row>

                    <Row className="justify-content-md-center">

                        <Col lg="4">
                            <p class="fs-3">Enter your code:</p>
                        </Col>

                    </Row>

                    <Row className="justify-content-md-center">

                        <Col lg="4">
                            <FloatingLabelsPassword handleChange={this.handleChange} val={this.state.password} name="password"/>
                        </Col>

                    </Row>

                </Container>

            </>

        )
        
    }

}

export default Login;
