import {ToggleButton,ButtonGroup} from "react-bootstrap"
import {Color} from '@utils/constants/Color.js'

export const MyButtonGroup = (props) => {

    return <>
                 <ButtonGroup>

                        { props.radios.map((radio, idx) => (
                          <ToggleButton
                            key={idx}
                            id={`radio-${idx}`}
                            type="radio"
                            name={radio.name}
                            value={radio.value}
                            checked={props.radioValue === radio.value}
                            onChange={props.handleChange}
                            style= { props.radioValue === radio.value ? {background: Color.PURPLE, border: Color.PURPLE} : 
                                                                        {background: Color.GRAY, border: Color.GRAY, color: Color.BLACK }  }
                          >
                            {radio.name}
                          </ToggleButton>
                        ))}

                </ButtonGroup>
            </>
 }
