import {Component} from "react"
import AuthenticationService from "@services/AuthenticationService.js"
import {LoginWithEmail,LoginWithPhone,Title} from './components/index.js'

class LoginManager extends Component{

        constructor(props){
            super(props)
                this.state = {
                    phone: '',
                    email: '',
                    password: '',
                    validated: false,
                    selectEmail: false,
                    selectPhone: false
                }
        }

        handleChange = (event) => {
            this.setState({
                [event.target.name] : event.target.value
            })
        }

        handleSubmit = (event) => {

            if(this.isValid(event)){
                //if(AuthenticationService.executeAuthenticationService(this.state.phonenumber, this.state.password)){
                    AuthenticationService.registerSuccessfulLogin(this.state.phonenumber)
                    this.props.history.push('/search/match/')
                    event.preventDefault();
                //}
            }
            
            event.preventDefault();

        }

        isValid = (event) =>{
            const form = event.currentTarget;
            if (form.checkValidity() === false) {
                this.setState({validated: true})
                 return false
            }
            
            return true
        }

      
        render(){

            return (

                <>

                    <Title/>



                    {  <LoginWithEmail handleSubmit={this.handleSubmit} 
                                    handleChange={this.handleChange}
                                    validated={this.state.validated}
                                    password={this.state.password}
                                    loginType={this.state.email}
                    />}

                    {<LoginWithPhone handleSubmit={this.handleSubmit} 
                                    handleChange={this.handleChange}
                                    validated={this.state.validated}
                                    password={this.state.password}
                                    loginType={this.state.phone}
                    />}

                </>

            )
            
        }

}

export default LoginManager;
