import React, { Component } from 'react'
import GridSystem from '@components/GridSystem.js';
import {FloatingLabelsPhone,FloatingLabelsUsername,FloatingLabelsEmail } from '@components/forms/floating_labels/index.js'
import {Navbar, Nav, Button, Container,Row,Col, Image} from "react-bootstrap"

class FindFriends extends Component {

  constructor(props){
    super(props)
        this.state = {

            username:   '',
            phone:  '', 
            email:  ''

        }
  }

  handleChange = (event) => {
    this.setState({
        [event.target.name] : event.target.value
  })
  }

  buildListViewRequests = () =>{

    const listViewRequest = [];
    let view

    for(let i = 0; this.state.filteredListRequests != null && i<this.state.filteredListRequests.length;i++){

       const view = this.buildViewRequests(this.state.filteredListRequests[i].target.username,
                                           this.state.filteredListRequests[i].target.id,
                                           this.state.filteredListRequests[i].target.image[0].content,
                                           this.confirmRequestClick)

        listViewRequest.push( view )

        }

      return listViewRequest

    }

    buildViewRequests = (username,id,image,confirmRequestClick) => {

      return  <div className='wrapper'>
                <Button onClick={e => AddFriend(e.target.id)}  id={id} className='c1 mt-4'>Add Friend</Button>
                <div className='c3 d-inline-block'>{username}</div> 
                <Image id={id+"image"}
                       onMouseEnter={e => this.viewMoreActions(e.target.id)} 
                       onMouseLeave={ e  =>  this.cancelViewMoreActions(e.target.id)}
                       src={`data:image/jpeg;base64,${image}`} 
                       className="grid-image mt-3 c2" />  
              </div> 
    }

  getNumRow = (colCount) =>{

    let rowcount

    if(this.state.filteredListRequests!=null){
      rowcount = Math.ceil(this.state.filteredListRequests.length / colCount) ;
     }else{
       rowcount = 0;
     }

     return rowcount
  }

    render() {

        const colCount = 2
        const rowcount = this.getNumRow(colCount)
        // const ListViewOptionFriends = this.buildListViewOptionFriends()

        return (

            <div className='mx-auto w-75 mt-5'>

                           <Container className='mt-4'>

                           <Row className="justify-content-md-center">

                                <Col lg="4">
                                       <FloatingLabelsPhone handleChange={this.handleChange} val={this.state.phone}/>
                                </Col>
    
                                <Col lg="4">
                                       <FloatingLabelsUsername handleChange={this.handleChange} val={this.state.username}/>
                                </Col>

                                <Col lg="4">
                                       <FloatingLabelsEmail handleChange={this.handleChange} val={this.state.email}/>
                                </Col>

                            </Row>

                            <Row className="justify-content-md-center">

                                    <Col lg="4">
                                               <Button className='w-50 mt-3'>Search</Button>
                                    </Col>
      
                            </Row>

                            <GridSystem rowcount = {rowcount} 
                                        colCount = {colCount} 
                                        // view={ListViewFriends}
                            />
  
                           
                           </Container>
            </div>

        )
    }
}

export default FindFriends
