import React, {Component} from "react";
import UserDataService from "@api/user/UserDataService";
import AuthenticationService from "@services/AuthenticationService.js"
import GridSystem from '@components/GridSystem.js';
import {Image, Button } from "react-bootstrap"

class ShowFriends extends Component {
  
  constructor(props){
    super(props)
        this.state = {
            id: AuthenticationService.getLoggedInUserId(),
            ListFriends: null
        }
    }

  componentDidMount(){
    this.refresh()
  }

  cancelFriendshipClick = async (friendId) =>{

    var requestParam = {
        params: {
          userId: Number(this.state.id) ,
          friendId: Number(friendId), 
      }
    }

    await UserDataService.deleteRelationship(this.state.id,requestParam);

    this.refresh()

  }

  refresh = async () =>{
    const res =  await UserDataService.getFriends(this.state.id);
    this.setState({ListFriends : res.data})
  }

  getNumRow = (colCount) =>{

    let rowcount

    if(this.state.ListFriends!=null){
      rowcount = Math.ceil(this.state.ListFriends.length / colCount) ;
     }else{
       rowcount = 0;
     }

     return rowcount
  }

  buildListViewFriends = () =>{

    const ListViewFriends = [];
    let view

    for(let i = 0; this.state.ListFriends != null && i<this.state.ListFriends.length;i++){

       const view = buildviewFriends(this.state.ListFriends[i].username,
                                this.state.ListFriends[i].id,
                                this.state.ListFriends[i].image[0].content,
                                this.cancelFriendshipClick)

        ListViewFriends.push( view )
        }

      return ListViewFriends
    }

    buildviewFriends = (username,id,image,cancelFriendshipClick) => {

      return  <div className='wrapper'>
                <Button onClick={e => cancelFriendshipClick(e.target.id)}  id={id} className='c1 mt-4'>cancel friendship</Button>
                <div className='c3 d-inline-block'>{username}</div> 
                <Image src={`data:image/jpeg;base64,${image}`} className="grid-image mt-3 c2"  />
              </div> 
 
    }
  

  render() {

    const colCount = 2
    const rowcount = this.getNumRow(colCount)
    const ListViewFriends = this.buildListViewFriends()

    return (

      <div className="mx-auto">

               <GridSystem rowcount = {rowcount} 
                           colCount = {colCount} 
                           view={ListViewFriends}
               />

      </div>

    );
  }
}

export default ShowFriends
