import Xarrow from "react-xarrows";
import React, { Component } from 'react'

class VisualizationSocialConnections extends Component {


    constructor(props){
        super(props)
            this.state = {
                conection: 
                        [  {id: "1", name:  "yoni",image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {id: "2", nane:  "maayan", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {id: "3", name:  "yanhiv", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {id: "4", name:  "kati", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'},
                           {id: "5", name:  "kati", image: 'https://i.pinimg.com/550x/b0/ac/ce/b0acce2169a511b6775f684cb28a027c.jpg'}
                         ],
                currentId : 0        
            }
    }


    render() {
        return (
            <div>

                {/* <div style={{display: "flex", justifyContent: "space-evenly", width: "100%"}}> */}
                     {this.state.conection.map(conect => {
                                                        if(conect)
                                                        <div>
                                                                  <img src={conect.image} class="rounded-circle" id={conect.id} style={{width: "100px", height: "100px"}} ></img>
                                                        </div>}
                                                        )}
                    <div class="container">
  <div class="row">
    <div class="col">
    <img src={this.state.conection[0].image} class="rounded-circle" id="1" style={{width: "100px", height: "100px"}} ></img>

    </div>
    <div class="col">
    <img src={this.state.conection[0].image} class="rounded-circle" id="2" style={{width: "100px", height: "100px"}} ></img>

    </div>
  </div>
  <div class="row">
    <div class="col">
    <img src={this.state.conection[0].image} class="rounded-circle" id="3" style={{width: "100px", height: "100px"}} ></img>

    </div>
    <div class="col">
    <img src={this.state.conection[0].image} class="rounded-circle" id="4" style={{width: "100px", height: "100px"}} ></img>

    </div>
    <div class="col">
    <img src={this.state.conection[0].image} class="rounded-circle" id="5" style={{width: "100px", height: "100px"}} ></img>

    </div>
  </div>
</div>

                     <Xarrow
                        start="1" //can be react ref
                        end="2" //can be react ref
                    />
                        <Xarrow
                        start="2" //can be react ref
                        end="3" //can be react ref
                    />
                {/* </div> */}

            </div>
        )
    }
}



