import {Component} from "react"
import AuthenticationService from "@services/AuthenticationService.js"
import {LoginWithEmail,LoginWithPhone,Title} from './components/index.js'
import {Button} from "react-bootstrap"
import {withRouter} from 'react-router-dom';

class LoginManager extends Component{

        constructor(props){
            super(props)
                this.state = {
                    phone: '',
                    email: '',
                    password: '',
                    validated: false,
                    selectEmail: false,
                    selectPhone: false
                }
        }

        handleChange = (event) => {
            this.setState({
                [event.target.name] : event.target.value
            })
        }

        handleSubmit = (event) => {

            if(this.isValid(event)){

                if(this.state.selectEmail){
                    this.emailAuthentication(event)
                }else{
                    this.phoneAuthentication(event)
                }
                
            }
            
            event.preventDefault();

        }

        isValid = (event) =>{
            const form = event.currentTarget;
            if (form.checkValidity() === false) {
                this.setState({validated: true})
                 return false
            }
            
            return true
        }

        startLoginWithEmail = ()=>{
            this.setState({selectEmail:true})
        }

        startLoginWithPhone = ()=>{
            this.setState({selectPhone:true})
        }

        emailAuthentication = async (event) =>{

            const result = await AuthenticationService.executeAuthenticationService(this.state.email, this.state.password)

            console.log(result)


        }

        phoneAuthentication = (event) =>{

            if(AuthenticationService.executeAuthenticationService(this.state.phone, this.state.password)){
                event.preventDefault();
                AuthenticationService.registerSuccessfulLogin(this.state.phone)
                this.props.history.push('/search/match/')
            
            }

        }
      
        render(){

            return (

                <>

                    <Title/>

                    { (! (this.state.selectEmail || this.state.selectPhone) ) &&
                      < Button size="lg" className='mb-2 mt-3 d-block mx-auto mt-5' onClick={this.startLoginWithPhone} >
                          To register by phone Click here
                        </Button>
                    }

                    { (! (this.state.selectEmail || this.state.selectPhone) ) && 
                      < Button size="lg" className='mb-2 mt-3 d-block mx-auto mt-5' onClick={this.startLoginWithEmail} >
                          To register by email click here
                      </Button>
                    }


                    { this.state.selectEmail && <LoginWithEmail handleSubmit={this.handleSubmit} 
                                                                handleChange={this.handleChange}
                                                                validated={this.state.validated}
                                                                password={this.state.password}
                                                                loginType={this.state.email}
                                                />}

                    { this.state.selectPhone && <LoginWithPhone handleSubmit={this.handleSubmit} 
                                                                handleChange={this.handleChange}
                                                                validated={this.state.validated}
                                                                password={this.state.password}
                                                                loginType={this.state.phone}
                    />}

                </>

            )
            
        }

}

export default withRouter(LoginManager)
